import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { Storage } from '@ionic/storage';
import { ModalController, AlertController, ToastController, Platform } from '@ionic/angular';
import { ModalControleComponent } from '../component/modal-controle/modal-controle.component';
//------
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

import { File } from '@ionic-native/file/ngx';
import { FileOpener } from '@ionic-native/file-opener/ngx';
import { formatDate } from '@angular/common';



@Component({
  selector: 'app-display-qc',
  templateUrl: './display-qc.page.html',
  styleUrls: ['./display-qc.page.scss'],
})
export class DisplayQcPage implements OnInit {

  pdfObj = null;

  file = new File();

  fileOpener = new FileOpener();
  searchBar: string;
  allQc = [{
    idQc: '0',
    idTemplate: '0',
    nomDuControle: '',
    idControles: [{
      id: '',
      nomControle: '',
      reponse: false
    }],
    auteur: '',
    date: '',
    time: ''
  }]

  displayedQc = [{
    idQc: '0',
    idTemplate: '0',
    nomDuControle: '',
    idControles: [{
      id: '',
      nomControle: '',
      reponse: false
    }],
    auteur: '',
    date: '',
    time: ''
  }]


  controles = [{
    idControle: '0',
    nomControle: 'none',
    description: 'null'
  }
  ];

  constructor(private storage: Storage, private activatedRoute: ActivatedRoute, public router: Router, public modalController: ModalController, public alertController: AlertController, public toastController: ToastController,
    private plt: Platform) {
  }


  ngOnInit() {
    this.loadData();
  }

  async loadData() {
    this.storage.get('all.qc').then((val) => {
      if (val != null) {
        this.allQc = val;
        this.allQc.shift()
        console.log(this.allQc);
        this.displayedQc = val;
        this.displayedQc.shift()
      }
    });

    this.storage.get('controles').then((val) => {
      if (val != null) {
        this.controles = val;
      }
    });

  }

  findControles() {
    this.displayedQc = []
    console.log(this.searchBar);


    this.allQc.forEach(element => {

      if (element.nomDuControle.toLowerCase().includes(this.searchBar.toLowerCase())) {
        console.log(element.nomDuControle + ' contains ' + this.searchBar)
        this.displayedQc.push(element);
      }
    });
    console.log(this.displayedQc);
  }

  doRefresh(event) {
    console.log('Begin async operation');
    this.searchBar = '';
    this.loadData();

    setTimeout(() => {
      console.log('Async operation has ended');
      event.target.complete();
    }, 2000);
  }


  viewUnLock(item) {
    this.presentModal(item)
  }


  async presentModal(item) {
    const modal = await this.modalController.create({
      component: ModalControleComponent,
      componentProps: {
        'id': item
      }
    });
    return await modal.present();
  }

  getControle(id) {

  }
  async checkControle(id) {
    console.log('id to search is' + id);
    let msg: string
    let title: string
    this.controles.forEach(element => {
      if (element.idControle == id) {
        msg = element.description
        title = element.nomControle
      }

    });
    const alert = await this.alertController.create({
      header: 'Information',
      subHeader: 'le controle ' + title,
      message: msg,
      buttons: ['OK']
    });

    await alert.present();
  }


  async presentToast() {
    const toast = await this.toastController.create({
      message: 'ce controle est verrouiller,impossible de le modifier.',
      duration: 2000
    });
    toast.present();
  }

  makePdf(id, autor, date, name, size, lock) {
    

    let options = { weekday: 'long', year: 'numeric',
    month: 'long', day: 'numeric' };
    let  dateTime = new Date().toLocaleDateString('fr-FR', options);
    let time = formatDate(new Date(), 'hh:mm:ss', 'en');
    let msg;
    if (lock) {
      msg = 'Ce doucement atteste du contrôle qualité effectué et ne peut plus être modifié.';
    }
    else {
      msg = 'Ce document n\'a pas encore été validé, les résultats sont donc susceptibles de changer.';
    }

    let result = [{
      nom: '0',
      description: 'none',
      result: ''
    }];
    result.length = 0;
    this.allQc.forEach(element => {
      if (element.idQc == id) {
        element.idControles.forEach(ctrl => {

          let ctrlDesc;
         
          this.controles.forEach(element => {
            if (element.idControle == ctrl.id) {
              ctrlDesc = element.description;
            }
          });

          if (ctrl.reponse) {
            result.push({
              nom: ctrl.nomControle,
              description: ctrlDesc,
              result: 'test validé'
            })
          }
          else {
            result.push({
              nom: ctrl.nomControle,
              description: ctrlDesc,
              result: 'test non validé'
            })
          }

        });
      }
    });


    pdfMake.vfs = pdfFonts.pdfMake.vfs;
    var docDefinition = {
      content: [
        { //0
          columns: [
            {
              image: 'data:image/png;base64,',
              width: 130,
            },
            {
              stack: [
                'Controle qualité numero° ' + id + '\n' + name,
                { text: 'effectué le ' + date + ' par ' + autor, style: 'subheader' },
              ],
              style: 'header'
            }
          ]
          
        },
        { //1
          text: [
            'Ce contrôle qualité contient ' + size + ' contrôles. Le fichier a été générer le ' + dateTime + ' à ' + time
          ]
        },
        { //2
          text: msg,
          margin: [0, 20],
        },
        { //3
          style: 'tableExample',
          table: {
            widths: [150, 200, '*'],
            body: [
              ['Description du controle', 'description ', 'résultat'],
              ['fixed-width cells have exactly the specified width',
                { text: 'nothing interesting here', italics: true, color: 'gray' },
                { text: 'nothing interesting here', italics: true, color: 'gray' }]
            ]
          }

        }

      ],
      styles: {
        header: {
          fontSize: 18,
          bold: true,
          alignment: 'right',
          margin: [0, 0, 0, 80]
        },
        subheader: {
          fontSize: 14
        },
        superMargin: {
          margin: [20, 0, 40, 0],
          fontSize: 15
        },
        tableExample: {
          margin: [0, 5, 0, 15]
        },
        tableHeader: {
          bold: true,
          fontSize: 13,
          color: 'black'
        }
      }

    }

    docDefinition.content[3].table.body.pop();
    let color;
    let percentageOk=0
    let percentageKo=0
    result.forEach(element => {
      if (element.result)
        if (element.result=='test validé'){
          color='green'
          percentageOk+=1;
        }
        else {
          color='red'
          percentageKo+=1;
        }
    
        docDefinition.content[3].table.body.push([element.nom, { text: element.description, italics: true, color: 'gray' }, { text: element.result, italics: true, color: color }])
    });
    docDefinition.content[2].text = docDefinition.content[2].text + '\n'+ percentageOk.toString() +' controle(s) sont vailde soit ' + percentageOk /(percentageOk + percentageKo)*100 +'% des controles effectués.'

    this.pdfObj = pdfMake.createPdf(docDefinition);
   // pdfMake.createPdf(docDefinition).open();
   this.downloadPdf();
  }
  downloadPdf() {
    if (this.plt.is('cordova')) {
      this.pdfObj.getBuffer((buffer) => {
        var blob = new Blob([buffer], { type: 'applicxation/pdf' });

        // Save the PDF to the data Directory of our App
        this.file.writeFile(this.file.dataDirectory, 'qc.pdf', blob, { replace: true }).then(fileEntry => {
          // Open the PDf with the correct OS tools
          this.fileOpener.open(this.file.dataDirectory + 'qc.pdf', 'application/pdf');
        })
      });
    } else {
      // On a browser simply use download!
      this.pdfObj.download();
    }
  }


}
